steps.run:
echo "üîç Running contract validation scenarios..."

# Test 1: Global cap enforcement
echo "üìä Testing global cap enforcement..."
node -e "
import { spawn } from 'node:child_process';
import { writeFile } from 'node:fs/promises';

const wrapper = \`
  globalThis.fetch = async (url) => {
    if (url.includes('/api/system/metrics')) {
      return new Response(JSON.stringify({
        queue_depth_by_platform: { reddit: 500, youtube: 400 },
        last_scan_times: {}
      }), { status: 200, headers: {'content-type':'application/json'}});
    }
    return new Response('[]', { status: 200, headers: {'content-type':'application/json'}});
  };
  import('./scripts/scan-plan.mjs').catch(e => { console.error(e); process.exit(1); });
\`;

await writeFile('__tmp_global_cap_test.mjs', wrapper);

const proc = spawn('node', ['__tmp_global_cap_test.mjs'], {
  env: {
    ...process.env,
    TARGET_URL: 'https://example.com',
    SUPABASE_URL: 'https://supabase.local',
    SUPABASE_SERVICE_ROLE_KEY: 'test',
    SCAN_GLOBAL_MAX: '800'
  }
});

let stdout = '';
proc.stdout.on('data', d => stdout += d.toString());

const code = await new Promise(res => proc.on('close', res));

if (stdout.includes('global_cap_reached')) {
  console.log('‚úÖ Global cap enforcement: PASSED');
} else {
  console.log('‚ùå Global cap enforcement: FAILED');
  console.log('Output:', stdout);
  process.exit(1);
}
" || exit 1

echo "CONTRACT_VALIDATION_STATUS=passed" >> $GITHUB_ENV

echo "üöÄ Running stress test scenarios..."

# Test with extreme platform counts
node -e "
import { spawn } from 'node:child_process';
import { writeFile } from 'node:fs/promises';

// Generate 50 platforms with various queue depths
const platforms = {};
for (let i = 0; i < 50; i++) {
  platforms[\`platform\${i}\`] = Math.floor(Math.random() * 200);
}

const wrapper = \`
  globalThis.fetch = async (url) => {
    if (url.includes('/api/system/metrics')) {
      return new Response(JSON.stringify({
        queue_depth_by_platform: ${JSON.stringify(platforms)},
        last_scan_times: {}
      }), { status: 200, headers: {'content-type':'application/json'}});
    }
    // Generate large supabase response
    const rows = Array.from({length: 1000}, (_, i) => ({
      source_platform: \`platform\${i % 50}\`,
      confidence_score: Math.random(),
      ingest_priority: 0,
      is_posted: false,
      is_approved: true
    }));
    return new Response(JSON.stringify(rows), { status: 200, headers: {'content-type':'application/json'}});
  };
  import('./scripts/scan-plan.mjs').catch(e => { console.error(e); process.exit(1); });
\`;

await writeFile('__tmp_stress_test.mjs', wrapper);

const start = Date.now();
const proc = spawn('node', ['__tmp_stress_test.mjs'], {
  env: {
    ...process.env,
    TARGET_URL: 'https://example.com',
    SUPABASE_URL: 'https://supabase.local',  
    SUPABASE_SERVICE_ROLE_KEY: 'test',
    PLATFORM_ALLOW: Object.keys(platforms).join(',')
  }
});

const code = await new Promise(res => proc.on('close', res));
const duration = Date.now() - start;

if (code === 0 && duration < 5000) {
  console.log(\`‚úÖ Stress test: PASSED (completed in \${duration}ms)\`);
} else {
  console.log(\`‚ùå Stress test: FAILED (exit code: \${code}, duration: \${duration}ms)\`);
  process.exit(1);
}
" || exit 1

echo "STRESS_TESTS_STATUS=passed" >> $GITHUB_ENV

echo "üìù Updating original PR with validation results..."

# Find PR associated with the commit SHA
COMMIT_SHA="${{ steps.context.outputs.commit_sha }}"

# Create validation results comment
cat > validation_comment.md << EOF
## üß™ Post-Remediation Validation Results

**Validation Time:** $(date -u +%Y-%m-%dT%H:%M:%SZ)  
**Triggered By:** ${{ steps.context.outputs.triggered_by }}

### üìä Results Summary
- **Overall Status:** ${{ steps.effectiveness.outputs.overall_status == 'success' && '‚úÖ Success' || steps.effectiveness.outputs.overall_status == 'partial' && '‚ö†Ô∏è Partial' || '‚ùå Failed' }}
- **Effectiveness Score:** ${{ steps.effectiveness.outputs.effectiveness_score }}/100
- **CI Ready:** ${{ steps.health-recheck.outputs.ci_ready == 'true' && '‚úÖ Yes' || '‚ùå No' }}

### üîç Component Validation
- **Lint:** ${{ steps.component-validation.outputs.lint_status == 'pass' && '‚úÖ' || '‚ùå' }}
- **Security:** ${{ steps.component-validation.outputs.security_status == 'pass' && '‚úÖ' || '‚ùå' }}
- **Build:** ${{ steps.component-validation.outputs.build_status == 'pass' && '‚úÖ' || '‚ùå' }}

${{ steps.rollback-check.outputs.rollback_required == 'true' && '### üö® Rollback Required\n**Reason:** Health critically low after remediation\n' || '' }}

> **Note:** This validation ran independently after Phase 3 auto-healing completed.
EOF

echo "‚úÖ Validation results prepared for PR update"
